import React from 'react';
import "./CoinInfo.css";



 class CoinInfo extends React.Component{
 constructor(props){  //eslint-disable-line no-useless-constructor
 	super(props);
 	this.inputRef=React.createRef()
 	this.state={
			data:{},
			isLoaded:false
		}
}
	handleChange(event) {
    this.setState({ text: event.target.value });
  };
	componentDidMount(){
		this.inputRef.current.focus()

		fetch('https://jsonplaceholder.typicode.com/posts/2').then(res=>res.json()).then(
			(user) => ( this.setState({data:user,isLoaded:true}))
			);
	}

 	clickHandler = () => {
 		alert(this.inputRef.current.value)
 	}

  render(){
	return(<div>

	<div className="decoration" align="center">
		<h1> Crypto Wiki </h1>
		<input type="text" ref={this.inputRef} onKeyUp={this.handleChange.bind(this)} placeholder="Enter The Crypto Currency Name" className="inputField" /> &nbsp;
		<button className="button" onClick={this.clickHandler}>Get Info About Coin</button><br/><br/>
		<h3>Search For a Coin</h3>
		<div align="center">
			<div className="frameSet" >
				<div  className="header"> {this.state.text} </div>
				<div className="info1">

					<div className="row">
						{this.state.isLoaded?this.state.data.name:'Loading Data. . . .'}	
					</div>
					{this.state.isLoaded?this.state.data.id:'Loading Data. . . .'}
				</div>
				<div className="info2">
				{this.state.isLoaded?this.state.data.symbol:'Loading Data. . . .'}

				</div>
			</div>
		</div>
	</div>

</div> );
};
};


export default CoinInfo;
